var dao = require('./..');

describe('Application', function() {

  it('application should have all props and methods defined', function() {
    var app = dao.App();

    app.plugins.should.be.a('object');
    app.options.should.be.a('object');

    app.root.should.exist;
    app.env.should.exist;

    app.initialized.should.exist;
    app.initList.should.exist;

    app.init.should.be.a('function');
    app.use.should.be.a('function');
    app.checkDeps.should.be.a('function');    
  })
})