var dao = require('./..');

describe('Dao', function() {
  it('should be a function', function() {
    dao.App.should.be.a('function');
  });

  it('that creates an object', function() {
    dao.App().should.be.a('object');
    (new dao.App()).should.be.a('object');
  });

  it('every time new object', function() {
    (dao.App()).should.not.equal(dao.App());
  })
});
